for d in */; do case "${d%/}" in anaconda3|cli|logs|mysql|restore|standards|test-runner|bkp*|shared*) continue ;; esac; cd "$d" || continue; if [ -d .git ] && git config -f .gitmodules --get-regexp '^submodule\.standards\.path$' >/dev/null 2>&1; then BRANCH="$(git symbolic-ref --quiet --short HEAD 2>/dev/null || echo main)"; git fetch origin >/dev/null 2>&1 || true; git submodule update --remote --merge standards >/dev/null 2>&1 || true; if ! git diff --quiet; then git add standards && git commit -m "Update standards submodule" && git push origin "$BRANCH"; fi; fi; cd - >/dev/null 2>&1; done

